#include <stdlib.h>
#include <stdio.h>
#include <string.h>

#include <test/utest.h>

#include <dogecoin/address.h>
#include <dogecoin/buffer.h>
#include <dogecoin/transaction.h>
#include <dogecoin/tx.h>
#include <dogecoin/utils.h>

void test_transaction()
{
    char* prvkey_wif = "ci5prbqz7jXyFPVWKkHhPq4a9N8Dag3TpeRfuqqC2Nfr7gSqx1fy";
    char* internal_address = "noxKJyGPugPRN4wqvrwsrtYXuQCk7yQEsy";

    char* external_address = "nbGfXLskPh7eM1iG5zz5EfDkkNTo9TRmde";
    char* scriptpubkey = "76a914d8c43e6f68ca4ea1e9b93da2d1e3a95118fa4a7c88ac";

    char* expected_hash_2 = "b4455e7b7b7acb51fb6feba7a2702c42a5100f61f61abafa31851ed6ae076074";
    char* expected_hash_10 = "42113bdc65fc2943cf0359ea1a24ced0b6b0b5290db4c63a3329c6601c4616e2";

    int expected_hash_2_vout_n = 1;
    int expected_hash_10_vout_n = 1;

    const char* txhex2 = "";
    const char* txhex10 = "";

    const char* unsigned_hexadecimal_transaction = "";
    const char* expected_single_input_signed_tx = "";
    const char* expected_finalized_tx = "";

// -------------------------------- address validation --------------------------------
    // prove internal p2pkh was derived from private key in wif format which
    // validates libdogecoins addressing functionality as both were generated by dogecoin core:
    u_assert_int_eq(verifyPrivPubKeypair(prvkey_wif, internal_address, true), 1);

    // validate p2pkh we will send 5 dogecoin to:
    u_assert_int_eq(verifyP2pkhAddress(external_address, strlen(external_address)), 1);

// -------------------------------- transaction generation & validation --------------------------------

    // instantiate a new working_transaction object by calling start_transaction()
    // which passes back index and stores in index variable
    int index = start_transaction();
    // add 1st input worth 2 dogecoin:
    add_utxo(1, expected_hash_2, expected_hash_2_vout_n);

    // get raw hexadecimal transaction to sign in the next steps
    char* raw_hexadecimal_transaction = get_raw_transaction(1);

    printf("raw transaction: %s\n", raw_hexadecimal_transaction);
    printf("txhex2: %s\n", txhex2);

    // add 2nd input worth 10 dogecoin:
    add_utxo(1, expected_hash_10, expected_hash_10_vout_n);

    // add output to transaction which is amount and address we are sending to:
    add_output(index, external_address, 500000000);
    // manually make change and send back to public key address
    make_change(index, prvkey_wif, external_address, 226000, 700000000);

    // get updated raw hexadecimal transaction
    raw_hexadecimal_transaction = get_raw_transaction(1);

    // assert complete raw hexadecimal transaction is equal to expected unsigned_hexadecimal_transaction
    u_assert_str_eq(raw_hexadecimal_transaction, unsigned_hexadecimal_transaction);

    // confirm total output value equals total utxo input value minus transaction fee
    raw_hexadecimal_transaction = finalize_transaction(index, external_address, 226000, 1200000000);
    
    // sign input index 0 of raw tx hex with script pubkey from utxo with sighash type of 1 (SIGHASH_ALL),
    // amount of 2 dogecoin represented as koinu (multiplied by 100 million) and with private key in wif format
    u_assert_int_eq(sign_raw_transaction(0, raw_hexadecimal_transaction, scriptpubkey, 1, 200000000, prvkey_wif), 1);

    // assert that our hexadecimal buffer (raw_hexadecimal_transaction) is equal to the expected transaction
    // with the first input signed:
    u_assert_str_eq(raw_hexadecimal_transaction, expected_single_input_signed_tx);

    // sign input index 1 of raw tx hex with script pubkey from utxo with sighash type of 1 (SIGHASH_ALL),
    // amount of 10 dogecoin represented as koinu (multiplied by 100 million) and with private key in wif format
    u_assert_int_eq(sign_raw_transaction(1, raw_hexadecimal_transaction, scriptpubkey, 1, 1000000000, prvkey_wif), 1);

    // assert that our hexadecimal bufer (raw_hexadecimal_transaction) is equal to the expected finalized
    // transaction with both inputs signed:
    u_assert_str_eq(raw_hexadecimal_transaction, expected_finalized_tx);

    // remove working transaction object from hashmap
    clear_transaction(index);
}
